import { useParams } from "react-router-dom";
import React, { useState, useEffect } from "react";
import { useSearchParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import {
  Row,
  Col,
  Image,
  ListGroup,
  Card,
  Button,
  ListGroupItem,
} from "react-bootstrap";
import Rating from "../components/Rating";
import { listProductDetails } from "../actions/productActions";
import axios from "axios";
import Loader from "../components/Loader";
import Message from "../components/Message";
// import products from "../products";
// console.log(products)
const ProductScreen = ({ match }) => {
  const dispatch = useDispatch();
  // console.log(match);
  // console.log(useParams());
  const { id } = useParams();

  //   const product = products.find((p) => String(p._id) === id);

  // if (!product) return null; // or fallback UI
  // const [product, setProduct] = useState({});

  useEffect(() => {
    dispatch(listProductDetails(id));
    // console.log("hello");
    // const fetchProduct = async () => {
    //   const { data } = await axios.get(`/api/products/${id}`);
    //   setProduct(data);
    // };
    // fetchProduct();
  }, [dispatch, id]);
  const productDetails = useSelector((state) => state.productDetails);
  const { loading, products } = productDetails;
  console.log(loading, products);
  return (
    <>
      
      <Link className="btn btn-dark my-3" to="/">
        Go Back
      </Link>

      <Row>
        <Col md={6}>
          <Image src={products.image} alt={products.name} fluid></Image>
        </Col>
        <Col md={3}>
          <ListGroup variant="flush">
            <ListGroup.Item>
              <h3>{products.name}</h3>
            </ListGroup.Item>
            <ListGroupItem>
              <Rating
                value={products.rating}
                text={`${products.numReviews} reviews`}
              />
            </ListGroupItem>
            <ListGroupItem>Price:${products.price}</ListGroupItem>
            <ListGroupItem>Description:{products.description}</ListGroupItem>
          </ListGroup>
        </Col>
        <Col md={3}>
          <Card>
            <ListGroup variant="flush">
              <ListGroupItem>
                <Row>
                  <Col>Price</Col>
                  <Col>
                    <strong>${products.price}</strong>
                  </Col>
                </Row>
              </ListGroupItem>
              <ListGroupItem>
                <Row>
                  <Col>Status:</Col>
                  <Col>
                    {products.countInStock > 0 ? `In Stock` : `Out of Stock`}
                  </Col>
                </Row>
              </ListGroupItem>
              <ListGroupItem>
                <Button
                  className="btn-block "
                  type="button"
                  disabled={products.countInStock === 0}
                >
                  ADD To Cart
                </Button>
              </ListGroupItem>
            </ListGroup>
          </Card>
        </Col>
      </Row>
    </>
  );
};

export default ProductScreen;
